import React, { useState } from "react";

function App() {
  const [goals, setGoals] = useState([
    { title: "Learn HTML", description: "Time to learn HTML..." },
    { title: "Learn CSS", description: "Time to learn CSS..." },
    { title: "Learn JavaScript", description: "Time to learn JavaScript..." }
  ]);

  const [newGoal, setNewGoal] = useState({ title: "", description: "" });

  const handleChange = (e) => {
    setNewGoal({ ...newGoal, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newGoal.title || !newGoal.description) return;
    setGoals([...goals, newGoal]);
    setNewGoal({ title: "", description: "" });
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Goal List</h1>
      <ul>
        {goals.map((goal, index) => (
          <li key={index}>
            <strong>{goal.title}</strong>: {goal.description}
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newGoal.title}
          onChange={handleChange}
        />
        <br />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={newGoal.description}
          onChange={handleChange}
        />
        <br />
        <button type="submit">Add Goal</button>
      </form>
    </div>
  );
}

export default App;
